mapper.select:
SELECT AB_NO, TO_CHAR(AB_DATE, 'YYYY-MM-DD') "AB_DATE", APC_NO, AB_AMOUNT, AB_MEMO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND APC_NO!=0
	AND AB_DATE=#{abDate}
	AND AB_DELETE='N'

SELECT AB_NO, TO_CHAR(AB_DATE, 'YYYY-MM-DD') "AB_DATE", AEC_NO, AB_AMOUNT, AB_MEMO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND AEC_NO!=0
	AND AB_DATE=#{abDate}
	AND AB_DELETE='N'

mapper.update:
UPDATE ACCOUNT_BOOK
SET AB_DELETE='Y'
WHERE AB_NO=#{abNo}

mapper.select:
SELECT AB_NO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND EXTRACT(YEAR FROM AB_DATE)=#{year}
    AND EXTRACT(MONTH FROM AB_DATE)=#{month}
    AND APC_NO!=0
    AND AB_DELETE='N'

SELECT AB_NO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND EXTRACT(YEAR FROM AB_DATE)=#{year}
    AND EXTRACT(MONTH FROM AB_DATE)=#{month}
    AND AEC_NO!=0
    AND AB_DELETE='N'

SELECT AB_AMOUNT
FROM ACCOUNT_BOOK
WHERE AB_NO=#{abNo}
