mapper.insert:
INSERT INTO JUMUN(ORDER_NO, MENU, AMOUNT, ORDER_DATE, TABLE_NO, PAY_YN) SELECT SEQ_JUMUN.NEXTVAL , J.* FROM (
SELECT #{item.MENU} AS MENU , #{item.AMOUNT} AS AMOUNT , SYSDATE AS ORDER_DATE , #{item.TABLE_NO} AS TABLE_NO, 'N' FROM DUAL
)J

INSERT INTO GROUP_MEMBER(GM_NO, GM_ID, G_NO, GM_DELETE) SELECT SEQ_GM.NEXTVAL , M.* FROM (
SELECT #{memberList.groupMemberId} AS GM_ID, #{memberList.groupNo} AS G_NO , 'N' FROM DUAL
)M

mapper.update:
UPDATE GROUP_TABLE 
SET G_TITLE = #{gTitle},
 	G_CON = #{gCon},
 	G_ORIGIN = #{gOrigin}
WHERE G_NO = #{gNo}

UPDATE GROUP_MEMBER
SET GM_ID = #{gmId},
	G_NO = #{gNo},
	GM_DELETE = #{gmDelete}
WHERE GM_NO = #{gmNO}

UPDATE GROUP_TABLE 
SET G_DELETE = 'Y'
WHERE G_NO = #{groupNo}

mapper.select:
