mapper.select:
SELECT GVM_NO, GVM.GV_NO, GVM.G_NO, GVM.GM_NO, GVM.GVI_NO AS GVI_NO
FROM GROUP_VOTE GV
JOIN GROUP_VOTE_MEMBER GVM ON(GV.GV_NO = GVM.GV_NO)
WHERE GV.GV_NO = #{gvNo}
AND GVM.GM_NO = #{gmNo}

mapper.delete:
DELETE FROM GROUP_VOTE_MEMBER
WHERE GV_NO = #{gvNo}
AND GM_NO = #{gmNo}

mapper.select:
	 	SELECT EXTRACT(MONTH FROM GA_DATE) AS GA_MONTH ,SUM(GA_AMOUNT) AS GA_TOTAL
		FROM GROUP_ACCOUNT
		WHERE G_NO = #{gNo}
		    AND EXTRACT(YEAR FROM GA_DATE)= #{year}
            AND EXTRACT(MONTH FROM GA_DATE)= #{month}
            AND GA_PRO = 'Y'
		    AND GA_DELETE='N'
		GROUP BY EXTRACT(MONTH FROM GA_DATE)

	 		SELECT EXTRACT(MONTH FROM GA_DATE) AS GA_MONTH ,SUM(GA_AMOUNT) AS GA_TOTAL
		FROM GROUP_ACCOUNT
		WHERE G_NO = #{gNo}
		    AND EXTRACT(YEAR FROM GA_DATE)= #{year}
            AND EXTRACT(MONTH FROM GA_DATE)= #{month}
            AND GA_EXP = 'Y'
		    AND GA_DELETE='N'
		GROUP BY EXTRACT(MONTH FROM GA_DATE)

	 		SELECT EXTRACT(MONTH FROM GA_DATE) AS GA_MONTH ,SUM(GA_AMOUNT) AS GA_TOTAL
			FROM GROUP_ACCOUNT
			WHERE G_NO = #{gNo}
		    AND EXTRACT(YEAR FROM GA_DATE)= #{year}
            AND EXTRACT(MONTH FROM GA_DATE)= #{month}
            AND GA_FEE = 'Y'
		    AND GA_DELETE='N'
		GROUP BY EXTRACT(MONTH FROM GA_DATE)

mapper.insert:
INSERT INTO GROUP_ACCOUNT_MEMBER(GAM_NO, G_NO, GM_NO, GA_NO, GAM_AMOUNT, GAM_DELETE, GAM_YN) SELECT SEQ_GAM.NEXTVAL , M.* FROM (
SELECT #{gamList.gNo} AS G_NO, #{gamList.gmNo} AS GM_NO , #{gamList.gaNo} AS GA_NO , #{gamList.gamAmount} AS GAM_AMOUNT ,#{gamList.gamDelete} AS GAM_DELETE ,#{gamList.gamYn} AS GAM_YN FROM DUAL
)M

mapper.select:
SELECT GA_NO, G_NO, GM_NO, GA_TITLE,TO_CHAR(TO_DATE(GA_DATE),'YYYY-MM-DD') AS GA_DATE, GA_CON, GA_AMOUNT, GA_PRO, GA_EXP, GA_FEE, GA_DELETE, GAS_YN
FROM GROUP_ACCOUNT
WHERE GA_NO = #{gaNo}

SELECT SUM(GAM_AMOUNT)
FROM GROUP_ACCOUNT_MEMBER
WHERE GA_NO = #{gaNo}

mapper.update:
UPDATE GROUP_ACCOUNT 
SET GAS_YN = 'N'
WHERE GA_NO = #{gaNo}

UPDATE GROUP_ACCOUNT_MEMBER
SET GAM_YN = #{gamYn}
WHERE GAM_NO = #{gamNo}

UPDATE GROUP_ACCOUNT
SET GA_DELETE = 'Y'
WHERE GA_NO = #{gaNo}
