mapper.update:
UPDATE ACCOUNT_BOOK
SET AB_DELETE='Y'
WHERE AB_NO=#{abNo}

mapper.select:
SELECT AB_NO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND EXTRACT(YEAR FROM AB_DATE)=#{year}
    AND EXTRACT(MONTH FROM AB_DATE)=#{month}
    AND APC_NO!=0
    AND AB_VIEW='Y'
    AND AB_DELETE='N'

SELECT AB_NO
FROM ACCOUNT_BOOK
WHERE ID=#{id}
	AND EXTRACT(YEAR FROM AB_DATE)=#{year}
    AND EXTRACT(MONTH FROM AB_DATE)=#{month}
    AND AEC_NO!=0
    AND AB_VIEW='Y'
    AND AB_DELETE='N'

SELECT AB_AMOUNT
FROM ACCOUNT_BOOK
WHERE AB_NO=#{abNo}

SELECT B.AEC_NO "AEC_NO", AEC_TITLE, SUM(AB_AMOUNT) "AB_SUM"
FROM ACCOUNT_BOOK B
JOIN A_EXPENDITURE_CATEGORY E ON(B.AEC_NO=E.AEC_NO)
WHERE ID=#{id}
    AND B.AEC_NO!=0
    AND EXTRACT(YEAR FROM AB_DATE)=#{year}
    AND EXTRACT(MONTH FROM AB_DATE)=#{month}
    AND AB_DELETE='N'
    AND AB_VIEW = 'Y'
GROUP BY B.AEC_NO, AEC_TITLE
