mapper.insert:
INSERT INTO MEMO
VALUES(SEQ_MM.NEXTVAL
	, #{id}
	, SYSDATE
	, #{memoCon}
	, #{mainNo}
	, #{memoType}
	, DEFAULT)

mapper.update:
UPDATE MEMO
SET MEMO_DELETE='Y'
WHERE MEMO_NO=#{memoNo}

UPDATE MONTHLY_PLAN
SET MP_VIEW='Y'
WHERE MEMO_NO=#{memoNo}

UPDATE MONTHLY_PLAN
SET MP_END=MP_END+1
WHERE MEMO_NO=#{memoNo}

UPDATE ACCOUNT_BOOK
SET AB_VIEW='Y'
WHERE MEMO_NO=#{memoNo}

mapper.insert:
INSERT INTO MAP_BOOKMARK
VALUES (SEQ_MB.NEXTVAL
